mapper.update:
update place set 
Place_Cert=1
where place_no=#{PlaceNo}

update place set 
Place_Cert=0
where place_no=#{PlaceNo}

mapper.select:
select count(*) from place_like
where place_no = #{placeNo}

	and member_id = #{memberId}

select place_no
from place
where place_name=#{placeName}

select count(*)
from place_grouping
where place_no = #{placeNo}
and group_detail_no = #{detailNo}

mapper.update:
update place set 
place_phone=#{placePhone}, 
place_comment=#{placeComment} 
where place_no=#{placeNo}
